com.mirth.connect.plugins.javascriptrule.script:
// se obtiene objeto completo 
const entry =  msg['entry'];
const jsonCompleto = msg;

const patientResult = {
	fullUrl:'',
	resource:{}	
};

const practitionerRoleResult = {
	fullUrl:'',
	resource:{}	
};

const practitionerResult = {
	fullUrl:'',
	resource:{}	
};

for each (var mssgH in entry) 
{

	if(mssgH.resource.resourceType == 'Patient'){
		patientResult.fullUrl = mssgH.fullUrl;
		patientResult.resource = mssgH.resource;
	}

	if(mssgH.resource.resourceType == 'PractitionerRole'){
		practitionerRoleResult.fullUrl = mssgH.fullUrl;
		practitionerRoleResult.resource = mssgH.resource;
	}

	if(mssgH.resource.resourceType == 'Practitioner'){
		practitionerResult.fullUrl = mssgH.fullUrl;
		practitionerResult.resource = mssgH.resource;
	}
};


/*
validacion de Patient
*/
var patient = patientResult;
var patientIdentifier = patient.resource.identifier;
var patientStatus="";
var rutPatientCont = 0;
const urlProfilePtnt = "PatientLE";


if(patientIdentifier.length > 0 && patientIdentifier != null){

	for(var i = 0; i < patientIdentifier.length; i++){

	if(patientIdentifier[i].type.coding[0].code == "1"){

		// Obtener el nmero del run sin dgito verificador
		var runSinDV = patientIdentifier[i].value;

		// Invertir el orden de los dgitos
		var runInvertido = runSinDV.split("").reverse().join("");

		// Variables para el clculo del dgito verificador
		var suma = 0;
		var factor = 2;

		// Realizar la suma ponderada
		for (var j = 0; j < runInvertido.length; j++) {
	  	suma += parseInt(runInvertido.charAt(j)) * factor;
	  	factor = factor == 7 ? 2 : factor + 1;
		}


		// Calcular el dgito verificador
		var resto = suma % 11;
		var dv = resto == 1 ? 'K' : resto == 0 ? '0' : 11 - resto;				

		if(dv != patientIdentifier[i].extension[0].valueString){
			rutPatientCont++;
		}

	}else{
			rutPatientCont++;		
		}
	}//fin for

	if(rutPatientCont == 0){
		patientStatus = "Validado";
	}else{
		patientStatus = "NoValido";
}

}else{
		patientStatus = "NoValido";
}




if(patientStatus.toString() == "NoValido"){
	var msgError = "Patient Run invalido ";
	var ErroResult =   JSON.stringify({  resourceType:"OperationOutcome", issue:[{ severity:"error",code:"processing",diagnostics:msgError}] }); 
	channelMap.put('resultValidations', ErroResult);
	return false;
}
if(patientStatus.toString() == "Validado"){
	var msgError = "validado";
	var ErroResult =   JSON.stringify({  resourceType:"OperationOutcome", issue:[{ severity:"validado",code:"processing",diagnostics:msgError}] }); 
	channelMap.put('resultValidations', ErroResult);	
	channelMap.put('ResulPatientValidation',JSON.stringify(patientResult.resource));
	channelMap.put('FullJson',JSON.stringify(jsonCompleto));
	return true;
}

