	serviceRequest,
	encounter,
	appointment,
	patient,
	questionnaireResponse,
	conditions[0],
	conditions[1],
	conditions[2],
	observation,	
	practitionerRole,
	practitioner,
	organization
	]
	};	
}

if(conditions.length == 2 && allergyIntolerance.fullUrl != null && allergyIntolerance.fullUrl != ""){
