properties.code:
/**
	[HCC] Personalized functions to map JSON/XML Patient resource to PID
*/
// Constants
// for treatment idenfifiers and domains
var gaExcludedIdTypes = ['787300']; // array including identifiers not to be replaced in case the patient already have it
var gaIdTypesforCountry = ['787300','808078', '688000','827884']; // array of identifier types to which we need to apply country extraction
var gaIdTypesforGuardian = ['827884']; // array of identifier types which are marked as guardian
var gaSystemsExcludedforReplaceCountry = ['2.16.840.1.113883.2.22.1.152.787300', '2.16.840.1.113883.2.22.1.152.827884']; // array of systems not to replace country with default
var gcDefaultContryforSystem = '0'; // default contry replacer for systems with country treatment 
// for extension url's
var gsExtUrlIdentifierCountry = 'http://hn.indra.es/fhir/StructureDefinition/identifier-country'; // extension url for indentify issuing country 
var gsExtUrlFatherFamilyName = 'http://hl7.org/fhir/StructureDefinition/humanname-fathers-family'; // extension url for father family name
var gsExtUrlMotherFamilyName = 'http://hl7.org/fhir/StructureDefinition/humanname-mothers-family'; // extension url for mothers family name
var gsExtUrlAddrCityCode = 'http://hn.indra.es/fhir/StructureDefinition/address-city-code'; // extension url for address city code
var gsExtUrlAddrStateCode = 'http://hn.indra.es/fhir/StructureDefinition/address-state-code'; // extension url for address state code
var gsExtUrlBiologicalGender = 'uri:hn:additional-data:identidad_genero'; // extension url for biological gender
var gsExtUrlStudyLevel = 'uri:hn:additional-data:nivel_instruccion'; // extension url for study level
var gsExtUrlTheoricDeathDate = 'uri:hn:additional-data:fecha_presunta_defuncion'; // extension url for theoric death date
var gsExtUrlPuebloIndigena = 'uri:hn:additional-data:pueblos'; // extension url for pueblos indigenas
var gsExtUrlBirthCountry = 'http://hn.indra.es/fhir/StructureDefinition/patient-birthcountry'; // extension url for patient birth country
var gsExtUrlNationality = 'http://hl7.org/fhir/StructureDefinition/patient-nationality'; // extension url for patient nationality

/**
	[HCC] Personalized mapping from JSON to PID for HCC
	@param patient JSON Patient resource
	@return pid HL7 PID segment
*/
function parsePatientJsonToPidHCC(patient) {
	var pid = new XML(<PID/>);

	// Identifiers
	parseIdentifiersJsonHCC(pid, patient);

	// Names
	var nameIdx=0;
	var pid9 = null;
	for each (name in patient['name']){	
		if ( nameIdx == 0 ) { // Nombre y apellidos
			var pid5 = new XML(<PID.5/>);

			// apellido 1
			var ext0 = name['extension'][0];
			if ( ext0 && ext0['valueString'] && ext0['url'] && ext0['url'].toString() == gsExtUrlFatherFamilyName ) {
				pid5['PID.5.1'] = ext0['valueString'].toString();
			}

			// nombre
			var nombres = name['given'][0].toString();
			var aNombres = nombres.split(' ');
			if ( aNombres.length == 2 ) {
				pid5['PID.5.2'] = aNombres[0];
				pid5['PID.5.3'] = aNombres[1];
			} else {
				pid5['PID.5.2'] = nombres;
			}

			pid5['PID.5.7'] = 'L';

			pid.appendChild(pid5);

			// apellido 2
			var ext1 = name['extension'][1];
			if ( ext1 && ext1['valueString'] && ext1['url'] && ext1['url'].toString() == gsExtUrlMotherFamilyName ) {
				var pid61 = ext1['valueString'].toString();		 
				if ( pid61.length > 0 ) {
					var pid6 = new XML(<PID.6/>);
					pid6['PID.6.1']=pid61;
					pid6['PID.6.7'] = 'L';
					pid.appendChild(pid6);
				}
			}
		} if ( nameIdx == 1 ) { // Nombre Social
			pid9 = new XML(<PID.9/>);
			var alias = name['text'];
			var aAlias = alias.split(' ');
			if ( aAlias.length == 2 ) {
				pid9['PID.9.1'] = aAlias[0];
				pid9['PID.9.2'] = aAlias[1];
			} else {
				pid9['PID.9.1'] = alias;
			}
		}
		nameIdx++;
	}

	// birth date
	try{
		var date = DateUtil.getDate('yyyy-MM-dd', patient['birthDate']);
		pid['PID.7'] = formatBirthDateToHl7(date);
	} catch (err){}

	// gender
	try{	
		pid['PID.8'] = map('administrativeGender','FHIR','HL7',patient['gender']);
	} catch (err){}	

	if ( pid9 ) {
		pid.appendChild(pid9);
	}

	// address
	try{
		for each ( address in patient['address'] ) {
			field = new XML(<PID.11/>);
			var line = '';
			if ( address['line'].length == 2 ) { // si hay dos lneas cada una a su sitio
				field['PID.11.1'] = address['line'][0].toString();
				field['PID.11.2'] = address['line'][1].toString();
			} else { // si hay un nmero distinto de lneas todas concatenadas
				line = address['line'].join(' ');
				if (line && line.length > 0 ) {
					field['PID.11.1'] = line;				
				}
			}
			// extract code and name from extensions
			var cityCode = null;
			var stateCode = null;
			for each ( addrext in address.extension ) {
				if ( addrext.url == gsExtUrlAddrCityCode ) {
					cityCode = addrext.valueString;
				} else if ( addrext.url == gsExtUrlAddrStateCode ) {
					stateCode = addrext.valueString;
				}
			}
			if ( address['city'] != undefined ) {
				if ( cityCode ) {
					field['PID.11.3']['PID.11.3.1'] = cityCode;
					field['PID.11.3']['PID.11.3.2'] = address['city'];
				} else {
					field['PID.11.3'] = address['city'];
				}
			}
			if ( address['state'] != undefined ) {
				if ( stateCode ) {
					field['PID.11.4']['PID.11.4.1'] = stateCode;
					field['PID.11.4']['PID.11.4.2'] = address['state'];
				} else {
					field['PID.11.4'] = address['state'];
				}
			}
			if (address['postalCode'] != undefined){
				field['PID.11.5'] = address['postalCode'];
			}
			if (address['country'] != undefined){
				field['PID.11.6'] =  address['country'];		
			}
			field['PID.11.7'] = map('addressUse', 'FHIR', 'HL7', address['use']);
			pid.appendChild(field);
		}
	} catch (err) {}

	// telecoms
	var aPid13 = [];
	var aPid14 = [];
	for each ( telecom in patient['telecom'] ) {
		var tUse = telecom.use;
		var tElement = map('contactPointSystem', 'FHIR', 'HL7', telecom.system);
		if ( tUse == 'work' ) { 
			var pid14 = new XML(<PID.14/>);
			pid14['PID.14.3'] = tElement;
			if ( tElement == 'Internet' ) {
				pid14['PID.14.4'] = telecom.value;
			} else {
				pid14['PID.14.7'] = telecom.value;
			}
			aPid14.push(pid14);
		} else { // by default we put as home
			var pid13 = new XML(<PID.13/>);
			pid13['PID.13.3'] = tElement;
			if ( tElement == 'Internet' ) {
				pid13['PID.13.4'] = telecom.value;
			} else {
				pid13['PID.13.7'] = telecom.value;
			}
			aPid13.push(pid13);
		}
	}

	// language
	for each ( comm in patient.communication ) {
		if ( comm.language && comm.language.coding && comm.language.coding.length > 0 ) {
			var pid15 = new XML(<PID.15/>);
			pid15['PID.15.1'] = comm.language.coding[0].code;
			pid.appendChild(pid15);

		}
	}

	// maritalStatus
	if ( patient.maritalStatus && patient.maritalStatus.coding && patient.maritalStatus.coding.length > 0 ) {
		var pid16 = new XML(<PID.16/>);
		pid16['PID.16.1'] = patient.maritalStatus.coding[0].code;
		pid16['PID.16.2'] = patient.maritalStatus.text;
		pid.appendChild(pid16);
	}

	// deceasedDateTime
	var pid29 = null;
	var pid30 = null;
	if ( patient.deceasedDateTime ) {
		pid29 = new XML(<PID.29/>);
		pid30 = new XML(<PID.30/>);
		var patDeathDate = DateUtil.getDate('yyyy-MM-dd', patient.deceasedDateTime);
		pid29['PID.29.1'] = formatBirthDateToHl7(patDeathDate);
		pid30['PID.30.1'] = 'Y';
	}

	// others ( biological gender, level of studies, theorical death date, pais nacimiento, nacionalidad, ...)
	var primeraNacionalidad = true;
	var pid22 = null;
	var pid23 = null;
	var pid261 = null;
	var pid262 = null;
	var pid28 = null;
	for each ( patext in patient.extension ) {
		var extURL = patext.url;
		if ( extURL == gsExtUrlBiologicalGender ) { // biological gender
			if ( extURL.valueCodeableConcept && extURL.valueCodeableConcept.coding && extURL.valueCodeableConcept.coding.length > 0 ) {
				pid261 = new XML(<PID.26/>);
				pid261['PID.26.1'] = extURL.valueCodeableConcept.coding[0].code;
				pid261['PID.26.2'] = extURL.valueCodeableConcept.text;
			}
		} else if ( extURL == gsExtUrlStudyLevel ) { // level of studies
			if ( extURL.valueCodeableConcept && extURL.valueCodeableConcept.coding && extURL.valueCodeableConcept.coding.length > 0 ) {
				pid262 = new XML(<PID.26/>);
				pid262['PID.26.1'] = extURL.valueCodeableConcept.coding[0].code;
				pid262['PID.26.2'] = extURL.valueCodeableConcept.text;
			}			
		} else if ( extURL == gsExtUrlTheoricDeathDate && !pid29) { // theorical death date when not having real deathdate
			if ( extURL.valueDateTime ) {
				pid29 = new XML(<PID.29/>);
				pid30 = new XML(<PID.30/>);
				var patDeathDate = DateUtil.getDate('yyyy-MM-dd', extURL.valueDateTime);
				pid29['PID.29.1'] = formatBirthDateToHl7(patDeathDate);
				pid30['PID.30.1'] = 'N';
			}
		} else if ( extURL == gsExtUrlPuebloIndigena ) { // pueblos indigenas
			if ( extURL.valueCodeableConcept && extURL.valueCodeableConcept.coding && extURL.valueCodeableConcept.coding.length > 0 ) {
				pid22 = new XML(<PID.22/>);
				pid22['PID.22.1'] = extURL.valueCodeableConcept.coding[0].code;
				pid22['PID.22.2'] = extURL.valueCodeableConcept.text;
			}
		} else if ( extURL == gsExtUrlBirthCountry ) { // pais de nacimiento
			if ( extURL.valueCodeableConcept && extURL.valueCodeableConcept.coding && extURL.valueCodeableConcept.coding.length > 0 ) {
				pid23 = new XML(<PID.23/>);
				pid23['PID.23.1'] = extURL.valueCodeableConcept.coding[0].code;
			}			
		} else if ( primeraNacionalidad && extURL == gsExtUrlNationality ) { // nacionalidad solo la primera
			primeraNacionalidad = false;
			if ( extURL.extension && extURL.extension.length > 0 && extURL.extension[0].valueCodeableConcept && extURL.extension[0].valueCodeableConcept.coding && extURL.extension[0].valueCodeableConcept.coding.length > 0 ) {
				pid28 = new XML(<PID.28/>);
				pid28['PID.28.1'] = extURL.extension[0].valueCodeableConcept.coding[0].code;
			}
		}
	}
	if ( pid22 ) {
		pid.appendChild(pid22);
	}
	if ( pid23 ) {
		pid.appendChild(pid23);
	}
	if ( pid261 ) {
		pid.appendChild(pid261);
	} else if ( pid262 ) {
		pid.appendChild(new XML(<PID.26/>)); //Create first item if not exists
	}
	if ( pid262 ) {
		pid.appendChild(pid262);
	}
	if ( pid28 ) {
		pid.appendChild(pid28);
	}
	if ( pid29 ) {
		pid.appendChild(pid29);
	}
	if ( pid30 ) {
		pid.appendChild(pid30);
	}

	return pid;
}

/**
	[HCC] Personalized mapping from JSON to PID for HCC (only Identifiers + masked patient name)
	@param patient JSON Patient resource
	@param domains Domain Application name
	@return pid HL7 PID segment
*/
function parsePatientIdentifierJsonToPidHCC(patient, domains) {	
	var pid = new XML(<PID/>);

	// Identifiers
	parseIdentifiersJsonHCC(pid, patient);

	// anonymous Name
	var field = new XML(<PID.5/>);				
	field['PID.5.7'] = 'S';
	pid.appendChild(field);

	return pid;
}

/**
	[HCC] Personalization for identifiers treatment for Json Patient resource
	@param pid The current pid segment to map
	@param patient JSON Node for patient to map
*/
function parseIdentifiersJsonHCC(pid, patient) {
	for each ( identifier in patient.identifier ) {
		var pid3 = new XML(<PID.3/>);
		var oid = identifier['system'];
		var pid341 = map('identifiers', 'OID', 'NAME', oid);
		var pid343 = map('identifiers', 'OID', 'TYPE', oid);
		// Country treatment
		if ( gaSystemsExcludedforReplaceCountry.indexOf(oid) == -1 ) {
			var aSystem = oid.split('.');
			var iNumSystemElem = aSystem.length;
			var sIdType = null;
			if ( iNumSystemElem > 0 ) {
				sIdType = aSystem[iNumSystemElem - 1];
			}
			if ( gaIdTypesforCountry.indexOf(sIdType) > -1  && iNumSystemElem > 1 ) {
				var countryCode = null;
				for each ( extension in identifier['extension'] ) {
					if ( extension.url == gsExtUrlIdentifierCountry ) {
						countryCode = extension.valueCodeableConcept.coding.code;
						break;
					}
				}
				if ( countryCode ) {
					aSystem.splice(iNumSystemElem - 2, 1, countrCode);
					oid = aSystem.join('.');
				}
			}
		}
		var pid342 = oid;
		pid3['PID.3.1'] = identifier['value'];
		pid3['PID.3.4']['PID.3.4.1'] = pid341;
		pid3['PID.3.4']['PID.3.4.2'] = pid342;
		pid3['PID.3.4']['PID.3.4.3'] = pid343;
		pid.appendChild(pid3);		
	}
}

/**
	[HCC] Personalized mapping from XML to PID for HCC (requires XML without namespaces Channel/Set Data Types/[choose destination]/Strip Namespaces checked)
	@param i The sequence of patient for PID segment
	@param patient XML Node for patient to map
	@return The pid serialized segment
*/
function parsePatientXmlToPidHCC( i, patient) {
	var pid = createSegment('PID');

	// sequence number
	pid['PID.1']['PID.1.1'] = i;
	// patient MPI ID
	pid['PID.2']['PID.2.1'] = patient.id.@value;

	// Documentos
	try {
		parseDocumentsHCC(pid, patient, patient.identifier);
	} catch (error) {
		logger.error("Error mapeando documentos: " + error)
	}

	// Nombre
	try {              
		var pid9 = parseNamesHCC(pid, patient.name);	
	} catch (error) {
		logger.error("Error mapeando nombre: " + error);
	}

	// Fecha nacimiento
	try {
		var date = DateUtil.getDate("yyyy-MM-dd", patient.birthDate.@value.toString());
		pid['PID.7']['PID.7.1'] = formatBirthDateToHl7(date);
	} catch (error) {
		logger.error("Error mapeando fecha nacimiento: " + error);
	}	

	// Genero
	try {
		pid['PID.8']['PID.8.1'] = map('administrativeGender','FHIR', 'HL7', patient.gender.@value.toString());
	} catch (error) {
		logger.error("Error mapeando genero: " + error);
	}	

	// append now pid9 if any 
	if ( pid9 ) {
		pid.appendChild(pid9);
	}

	// Direccion
	try {
		parseAdressHCC(pid, patient.address);
	} catch (error) {
		logger.error("Error mapeando direccin: " + error);
	}

	// telecoms
	var aPid13 = [];
	var aPid14 = [];
	for each ( telecom in patient['telecom'] ) {
		var tUse = telecom.use.@value.toString();
		var tElement = map('contactPointSystem', 'FHIR', 'HL7', telecom.system.@value.toString());
		if ( tUse == 'work' ) { 
			var pid14 = new XML(<PID.14/>);
			pid14['PID.14.3'] = tElement;
			if ( tElement == 'Internet' ) {
				pid14['PID.14.4'] = telecom.value.@value.toString();
			} else {
				pid14['PID.14.7'] = telecom.value.@value.toString();
			}
			aPid14.push(pid14);
		} else { // by default we put as home
			var pid13 = new XML(<PID.13/>);
			pid13['PID.13.3'] = tElement;
			if ( tElement == 'Internet' ) {
				pid13['PID.13.4'] = telecom.value.@value.toString();
			} else {
				pid13['PID.13.7'] = telecom.value.@value.toString();
			}
			aPid13.push(pid13);
		}
	}
	for each (var node in aPid13) {
		pid.appendChild(node);
	}
	for each (var node in aPid14) {
		pid.appendChild(node);
	}

	// language
	var language = patient.communication.language.coding.code.@value.toString();
	if ( language.length > 0 ) {
		var pid15 = new XML(<PID.15/>);
		pid15['PID.15.1'] = language;
		pid.appendChild(pid15);
	}

	// maritalStatus
	var maritalStatus = patient.maritalStatus.coding.code.@value.toString();
	if ( maritalStatus.length > 0 ) {
		var pid16 = new XML(<PID.16/>);
		pid16['PID.16.1'] = maritalStatus;
		pid16['PID.16.2'] = patient.maritalStatus.text.@value.toString();
		pid.appendChild(pid16);
	}

	// others
	// pueblos indigenas
	var puebloIndigena = patient.extension.(@url==gsExtUrlPuebloIndigena).valueCodeableConcept.coding.code.@value.toString();
	if ( puebloIndigena.length > 0 ) {
		var pid22 = new XML(<PID.22/>);
		pid22['PID.22.1'] = puebloIndigena;
		pid22['PID.22.2'] = patient.extension.(@url==gsExtUrlPuebloIndigena).valueCodeableConcept.text.@value.toString();
		pid.appendChild(pid22);
	}

	// pais de nacimiento
	var paisNacimiento = patient.extension.(@url==gsExtUrlBirthCountry).valueCodeableConcept.coding.code.@value.toString();
	if ( paisNacimiento.length > 0 ) {
		var pid23 = new XML(<PID.23/>);
		pid23['PID.23.1'] = paisNacimiento;
		pid.appendChild(pid23);
	}

	// biological gender
	var biologicalGender = patient.extension.(@url==gsExtUrlBiologicalGender).valueCodeableConcept.coding.code.@value.toString();
	var pid26 = new XML(<PID.26/>);
	if ( biologicalGender.length > 0 ) {
		pid26['PID.26.1'] = biologicalGender;
		pid26['PID.26.2'] = patient.extension.(@url==gsExtUrlBiologicalGender).valueCodeableConcept.text.@value.toString();
	}
	pid.appendChild(pid26);

	// level of studies
	var instructionLevel = patient.extension.(@url==gsExtUrlStudyLevel).valueCodeableConcept.coding.code.@value.toString();
	pid26 = new XML(<PID.26/>);
	if ( instructionLevel.length > 0 ) {
		pid26['PID.26.1'] = instructionLevel;
		pid26['PID.26.2'] = patient.extension.(@url==gsExtUrlStudyLevel).valueCodeableConcept.text.@value.toString();
	}
	pid.appendChild(pid26);

	// Nacionalidad
	var nacionalidad = patient.extension.(@url==gsExtUrlNationality).extension.valueCodeableConcept.coding.code.@value.toString();
	if ( nacionalidad.length > 0 ) {
		if ( nacionalidad.length > 3 ) { // hay ms de una tomar slo la primera
			nacionalidad = patient.extension.(@url==gsExtUrlNationality)[0].extension.valueCodeableConcept.coding.code.@value.toString()
		}
		var pid28 = new XML(<PID.28/>);
		pid28['PID.28.1'] = nacionalidad;
		pid.appendChild(pid28);
	}

	// deceasedDateTime
	var pid29 = null;
	var pid30 = null;
	var patDeathDate = patient.deceasedDateTime.@value.toString();
	if ( patDeathDate.length > 0 ) {
		pid29 = new XML(<PID.29/>);
		pid30 = new XML(<PID.30/>);
		patDeathDate = DateUtil.getDate('yyyy-MM-dd', patDeathDate);
		pid29['PID.29.1'] = formatBirthDateToHl7(patDeathDate);
		pid30['PID.30.1'] = 'Y';
	}
	// theorical death date whenever not real death date exists
	if ( !pid29 ) {
		var patTheoricalDeathDate = patient.extension.(@url==gsExtUrlTheoricDeathDate).valueDateTime.@value.toString();
		if ( patTheoricalDeathDate.length > 0 ) {
			pid29 = new XML(<PID.29/>);
			pid30 = new XML(<PID.30/>);
			patTheoricalDeathDate = DateUtil.getDate('yyyy-MM-dd', patTheoricalDeathDate);
			pid29['PID.29.1'] = formatBirthDateToHl7(patTheoricalDeathDate);
			pid30['PID.30.1'] = 'N';
		}
	}
	if ( pid29 && pid30 ) {
		pid.appendChild(pid29);
		pid.appendChild(pid30);
	}

	return pid;
}

/**
	[HCC] Personalization for identifiers treatment
	@param pid The current pid segment to map
	@param patient XML Node for patient to map
	@param documents XML Nodes for documents to map
*/
function parseDocumentsHCC(pid, patient, documents) {
	for each( document in documents ) {
		var pid3 = new XML(<PID.3/>);		
		var oid = document.system.@value.toString();			
		var pid341 = map('identifiers', 'OID', 'NAME', oid);
		var pid343 = map('identifiers', 'OID', 'TYPE', oid);
		// Country treatment for oid
		if ( gaSystemsExcludedforReplaceCountry.indexOf(oid) == -1 ) {
			var aSystem = oid.split('.');
			var iNumSystemElem = aSystem.length;
			var sIdType = null;
			if ( iNumSystemElem > 0 ) {
				sIdType = aSystem[iNumSystemElem - 1];
			}
			if ( gaIdTypesforCountry.indexOf(sIdType) > -1  && iNumSystemElem > 1 ) {
				var countryCode = document.extension.(@url==gsExtUrlIdentifierCountry).valueCodeableConcept.coding.code.@value.toString();
				if ( countryCode.length() > 0 ) {
					aSystem.splice(iNumSystemElem - 2, 1, countrCode);
					oid = aSystem.join('.');
				}
			}
		}
		var pid342 = oid;
		pid3['PID.3.1'] = document.value.@value;
		pid3['PID.3.4']['PID.3.4.1'] = pid341;
		pid3['PID.3.4']['PID.3.4.2'] = pid342;
		pid3['PID.3.4']['PID.3.4.3'] = pid343;
		pid.appendChild(pid3);
	}
}

/**
	[HCC] Personalization for names treatment
	@param pid The current pid segment to map
	@param names XML Nodes for names to map
	@return PID9 if created must be appened after prior
*/
function parseNamesHCC(pid, names){
	var nameIdx = 0;
	var pid9 = null;
	for each( name in names ) {
		if ( nameIdx == 0 ) { // Surnames and Names
			var pid5 = new XML(<PID.5/>);
			// Primer Apellido
			pid5['PID.5.1'] = name.extension.(@url==gsExtUrlFatherFamilyName).valueString.@value.toString();

			// Nombres
			var nombres = name.given[0].@value.toString();
			var aNombres = nombres.split(' ');
			if ( aNombres.length == 2 ) {
				pid5['PID.5.2'] = aNombres[0];
				pid5['PID.5.3'] = aNombres[1];
			} else {
				pid5['PID.5.2'] = nombres;
			}
			pid5['PID.5.7'] = map('nameUse', 'FHIR', 'HL7', name.use.@value);	
			pid.appendChild(pid5);

			// Segundo Apellido
			var pid61 = name.extension.(@url==gsExtUrlMotherFamilyName).valueString.@value.toString();		 
			if ( pid61.length > 0 ) {
				var pid6 = new XML(<PID.6/>);
				pid6['PID.6.1']=pid61;
				pid6['PID.6.7'] = 'L';
				pid.appendChild(pid6);
			}
		} else if ( nameIdx == 1 ) { // Social Name
			pid9 = new XML(<PID.9/>);
			var alias = name.text.@value.toString();
			var aAlias = alias.split(' ');
			if ( aAlias.length == 2 ) {
				pid9['PID.9.1'] = aAlias[0];
				pid9['PID.9.2'] = aAlias[1];
			} else {
				pid9['PID.9.1'] = alias;
			}
		}
		nameIdx++;
	} 
	return pid9;
}

/**
	[HCC] Personalization for address treatment
	@param pid The current pid segment to map
	@param addresses XML Nodes for address to map
*/
function parseAdressHCC(pid, addresses){
	var field;
	for each ( address in addresses ) {
		field = new XML(<PID.11/>);
		var line = '';
		if ( address.line.length() == 2 ) { // si hay dos lneas cada una a su sitio
			field['PID.11.1'] = address.line[0].@value.toString();
			field['PID.11.2'] = address.line[1].@value.toString();
		} else { // si hay un nmero distinto de lneas todas concatenadas
			var aLines = [];
			for each ( line in address.line ) {
				aLines.push(line.@value.toString());
			}
			line = aLines.join(' ');
			if ( line && line.length > 0 ) {
				field['PID.11.1'] = line;				
			}
		}

		// extract code and name from extensions
		var cityCode = address.extension.(@url==gsExtUrlAddrCityCode).valueString.@value.toString();
		var stateCode = address.extension.(@url==gsExtUrlAddrStateCode).valueString.@value.toString();
		if ( cityCode && cityCode.length > 0 ) {
			field['PID.11.3']['PID.11.3.1'] = cityCode;
			field['PID.11.3']['PID.11.3.2'] = address.city.@value.toString();
		} else {
			field['PID.11.3'] = address.city.@value.toString();
		}
		if ( stateCode && stateCode.length > 0 ) {
			field['PID.11.4']['PID.11.4.1'] = stateCode;
			field['PID.11.4']['PID.11.4.2'] = address.state.@value.toString();
		} else {
			field['PID.11.4'] = address.state.@value.toString();
		}
		field['PID.11.5'] = address.postalCode.@value.toString();
		field['PID.11.6'] = address.country.@value.toString();

		field['PID.11.7'] = map('addressUse', 'FHIR', 'HL7', address.use.@value.toString());
		pid.appendChild(field);
	}
}

/**
[HCC] Mapping extensions for countries, ...
*/

var countries = new Packages.java.util.HashMap();
var nations = new Packages.java.util.HashMap();

function loadCountries() {
	countries.clear();
	var result = sendHttpGet($g('hncat.fhir.url') + '/ValueSet/pais?_format=xml');	
	var respString = EntityUtils.toString(result.getEntity());		
	var respXml = new XML(respString);
	var total = respXml.*::compose.*::include.*::concept.length();;
	for each (var pais in respXml.*::compose.*::include.*::concept) {
		countries.put(pais.*::code.@value.toString(), pais.*::display.@value.toString());
	}
	globalMap.put('countries', countries);	
}

function loadNations() {
	nations.clear();
	var result = sendHttpGet($g('hncat.fhir.url') + '/ValueSet/nacionalidad?_format=xml');	
	var respString = EntityUtils.toString(result.getEntity());		
	var respXml = new XML(respString);
	var total = respXml.*::compose.*::include.*::concept.length();;
	for each (var pais in respXml.*::compose.*::include.*::concept) {
		nations.put(pais.*::code.@value.toString(), pais.*::display.@value.toString());
	}
	globalMap.put('nations', nations);	
}

/**
	[HCC] Load Properties

	@param {Any} base_path - 
	@return {Any} 
*/
// Carga ficheros HCC
var mirth = 'mirth/';
var filesHCC = [mirth+'ihe-hcc.properties'];

function loadPropsHCCFromPath(base_path){

	logger.info("base_path HCC:"+base_path);
	var config = new Packages.org.apache.commons.configuration.CompositeConfiguration(); 

	// Configuracion de entorno. Lee las variables del sistema. Tienen prioridad sobre las definidas en properties
	config.addConfiguration(new Packages.org.apache.commons.configuration.EnvironmentConfiguration());

	// Configuration. Valores por defecto
	for each (entry in configurationMap.entrySet().toArray()){
		$g(entry.getKey(), entry.getValue());
		// Se inyectan sin punto tambien, Mirth no permite . en las claves de los destinos
		$g(entry.getKey().replace('.', '_'), entry.getValue());
	}

	// Ficheros de configuracion
	for each (file in filesHCC){
		try{			
			config.addConfiguration(new Packages.org.apache.commons.configuration.PropertiesConfiguration(base_path + '/' + file));
		}catch (e){
			//logger.warn(e);
		}
	}

	loadPropertiesHCC(config);
}

function loadPropertiesHCC(config){
	//Mapeo de propiedades
	var keys = config.getKeys();
	while (keys.hasNext()){
		var key = keys.next();
		var value = config.getString(key);
		key = key.toLowerCase();
		$g(key,value);
		// Se inyectan sin punto tambien, Mirth no permite . en las claves de los destinos
		$g(key.replace('.', '_'),value);
	}
}

