// Genera los campos a partir del mensaje HL7 v2.x
function generateFieldsAtnaHl7 (msg) {
	// Campos necesarios para ATNA messageAudit

	var sourceUserID = msg['MSH']['MSH.4']['MSH.4.1'].toString() + '|' + msg['MSH']['MSH.3']['MSH.3.1'].toString();
	$g('sourceUserID',sourceUserID);

	var sourceHost = "mirth";
	$g('sourceHost', sourceHost);

	var destinationUserID = msg['MSH']['MSH.6']['MSH.6.1'].toString() + '|' + msg['MSH']['MSH.5']['MSH.5.1'].toString();
	$g('destinationUserID',destinationUserID);

	var participantObjectDetail = msg['MSH']['MSH.10']['MSH.10.1'].toString();
	$g('participantObjectDetail',participantObjectDetail);

	var query = SerializerFactory.getSerializer('HL7V2').fromXML(msg);
	$g('consulta',query);
}

// Genera los campos a partir de los datos de otro canal o del fichero de propiedades
function generateFieldsAtnaFhir () {		

	var sourceUserID = $('msh4.sending.facility').toString() + '|' +  $('msh3.sending.application').toString();
	$g('sourceUserID',sourceUserID);

	var sourceHost = "mirth";
	$g('sourceHost', sourceHost);

	var destinationUserID = $('msh6.receiving.facility').toString() + '|' +  $('msh5.receiving.application').toString();
	$g('destinationUserID',destinationUserID);

}


function sendMessageAtna(type, id){

	var port = Packages.java.lang.Integer.parseInt($('atna.port'));
	var ssl = Packages.java.lang.Boolean.parseBoolean($('atna.ssl'));
	var sar = Packages.es.indra.hn.hnatna.notificator.SyslogAuditRepositoryFactory.create($('atna.app'),null,null,
	null,port,$('atna.protocol'),null,ssl,$('atna.host'));

	var sourceUserID = $('sourceUserID');
	var sourceHost = $('sourceHost');
	var destinationUserID = $('destinationUserID');
	var participantObjectDetail = $('participantObjectDetail');
	var consulta = $('consulta');
	var participantObjectID = $('participantObjectID');
	var destinationHost = $('hnpob_fhir_url');
	var patientIds = [];
	try{
		var patientId = id;
		patientId = patientId.replace('&', '&amp;');
		patientIds[0] = patientId;
	} catch (err) {}

	var notif = new Packages.es.indra.hn.hnatna.notificator.AtnaNotificatorImpl(sar);

	//logger.debug("sourceUserID: " + sourceUserID + " destinationUserID: " + destinationUserID + " destinationHost: " + destinationHost + " destinationUserID: " + destinationUserID + " consulta: " + consulta + " participantObjectDetail: " + participantObjectDetail + " patientIds" + patientIds[0]);  
	if (type == 'ITI9-Manager'){
		notif.sendPIXQuery_ITI9_Manager(sourceUserID, sourceHost, destinationUserID, "destinationAltUserId", destinationHost, destinationUserID, "323", "D", consulta, participantObjectDetail, patientIds);
	}
	if (type == 'ITI21-Manager'){
		notif.sendPDQ_ITI21_Supplier(sourceUserID, sourceHost, destinationUserID, "destinationAltUserId", destinationHost, destinationUserID, "323", "D", consulta, participantObjectDetail, patientIds);
	}
	if (type == 'ITI83-Manager'){
		notif.sendPIXmQuery_ITI83_Manager(sourceUserID, sourceHost, destinationUserID, "destinationAltUserId", destinationHost, destinationUserID, "323", "D", consulta, participantObjectDetail, patientIds);
	}
	if (type == 'ITI83-Consumer'){
		notif.sendPIXmQuery_ITI83_Consumer(sourceUserID, sourceHost, destinationUserID, "destinationAltUserId", destinationHost, destinationUserID, "323", "D", consulta, participantObjectDetail, patientIds, 'humanUserId');
	}	
	if (type == 'ITI78-Supplier'){
		notif.sendPDQm_ITI78_Supplier(sourceUserID, sourceHost, destinationUserID, "destinationAltUserId", destinationHost, destinationUserID, "323", "D", consulta, participantObjectDetail, patientIds);
	}
	if (type == 'ITI78-Consumer'){
		notif.sendPDQm_ITI78_Consumer(sourceUserID, sourceHost, destinationUserID, "destinationAltUserId", destinationHost, destinationUserID, "323", "D", consulta, participantObjectDetail, patientIds, 'humanUserId');
	}	
	logger.info("mensaje enviado (or something)");
}


