			/*
			var pid341 = map('PID.3.4.1', 'HN', 'HL7', hnCode);
			var pid342 = map('PID.3.4.2', 'HN', 'HL7', hnCode);
			var pid343 = map('PID.3.4.3', 'HN', 'HL7', hnCode);
			*/
			var pid341 = map('identifiers', 'OID', 'NAME', oid);			
			var pid342 = oid;
