	[HN] security
*/

importPackage(org.apache.http.impl.client);
importPackage(org.apache.http.client.methods);
importPackage(org.apache.http.client.entity);
importPackage(org.apache.http.entity);
importPackage(org.apache.http.util);
importPackage(java.util);
importPackage(org.apache.http.message);
importPackage(java.lang);
importPackage(javax.json);
importPackage(java.io);

var key = 'mirth.security.token';

function removeToken(){
	globalMap.remove(key);
}

function validToken(token){	
	try{		
		token = token + "";
		var payload = token.split('.')[1];	
		var decode = FileUtil.decode(payload);	
		var payloadDecode = new java.lang.String(decode, "UTF-8");	
		var reader = Json.createReader(new StringReader(payloadDecode));
		var json = reader.readObject();		
		// Se obtiene la fecha de expiracion del campo exp del token
		var segundosExp = json.getInt("exp");	
		logger.debug("segundosExp: " + segundosExp);		
		// Se resta 3 minutos para asegurar que el token sigue siendo valido para los diferentes destination del canal
		segundosExp = segundosExp - 180;		
		var expDate = new Date(0);	
		expDate.setUTCSeconds(segundosExp);
		logger.debug("Fecha expiracion token: " + expDate);
		if (expDate <= new Date()){
			return false;	
		} else {
			return true;
		}	
	} catch (err){
		return false;	
	}
}

// deprecated. Esta funcion no deberia de usarse. 
// Tenemos que usar getClientCredentialsToken();
function getToken(moduleUrl){
	var token = $g(key);	
	logger.debug('key: ' + key);
	logger.debug('token obtenido: ' + token);
	if (token == null || token.length == 0){
		logger.debug('get token');
		token = getClientCredentialsToken();
		$g(key, token);
	}
	return token;
}


function getClientCredentialsToken(){	
	var token = globalMap.get(key);	
	//logger.debug("TOKEN: " + token);
	if (token != null && validToken(token)){
		return token;
	}

	logger.debug("token caducado. Obteniendo token nuevo.")
	var client = getHttpClient();
	var method = new HttpPost($g('keycloak.url.initial'));
	var params = new ArrayList();
	params.add(new BasicNameValuePair("grant_type", "password"));
	params.add(new BasicNameValuePair("client_id", $g('keycloak.client')));
	params.add(new BasicNameValuePair("username", $g('keycloak.user')));
	var pass = Packages.java.lang.String(FileUtil.decode($g('keycloak.pass').toString()));
	params.add(new BasicNameValuePair("password", pass));
	method.setEntity(new UrlEncodedFormEntity(params));
	var response = client.execute(method);
	switch (response.getStatusLine().getStatusCode()) {
		case 200:
			var entity = response.getEntity();
			var content = EntityUtils.toString(entity);
			var reader = Json.createReader(new StringReader(content));
			var json = reader.readObject();
			var token = json.getString("access_token");
			//validToken(token);
			globalMap.put(key, token);
			return token;
			//return json.getString("access_token");
		default:
			logger.error(response);
			throw "Error. No se ha podido obtener el token";
		}
}

function getClientCredentialsTokenByToken(requestJWT){

	var client = getHttpClient();

	var method = new HttpPost('http://homologacionhcc.indra.es/auth/realms/hnrole/protocol/openid-connect/token-from-external-jwt');
	logger.debug('method: ' + method);
	method.addHeader("Content-Type", "application/x-www-form-urlencoded");

	//create entity
	var entity = new StringEntity("client_id=hnrole&subject_token=" + requestJWT); 

	//add entity to BODY
	method.setEntity(entity);	
	var response = client.execute(method);
	logger.debug('response: ' + response);
	switch (response.getStatusLine().getStatusCode()) {
		case 200:
			var entity = response.getEntity();
			var content = EntityUtils.toString(entity);
			var reader = Json.createReader(new StringReader(content));
			var json = reader.readObject();
			return json.getString("access_token");
		default:
			logger.error(response);
			throw "Error. No se ha podido obtener el token";
		}
}

/**

	[HN] Validate Permits

*/
function validatePerm(token,perm) {
	logger.debug("Code Templates - validatePerm - token : " + token);
	logger.debug("Code Templates - validatePerm - perm : " + perm);
	var jwt = new String(token);

	//Obtenemos el payload 
	var payload =jwt.split('.')[1];
	logger.debug("Code Templates - validatePerm - payload : " + payload); 
	var payloadDecoded = new Packages.java.lang.String(Packages.java.util.Base64.getDecoder().decode(payload),"UTF-8");

	//Obtenemos los permisos
	var payLoadJson = JSON.parse(payloadDecoded);
	logger.debug("Code Templates - validatePerm - payLoadJson - permisos: " + payLoadJson["realm_access.roles"]);

	//Comprobamos que no venga vaco.
	if(isEmptyStr(payLoadJson["realm_access.roles"])) {
		return false;
	}
	var realm_access = new String(payLoadJson["realm_access.roles"].toString());
	var permArray = realm_access.split(',');

	for (var i = 0; i < permArray.length; i++) {
		if (permArray[i].toString() == perm.toString()) {
			return true;
		}
	}
	return false;
}

