	[HN] XDS

*/

const textRepository = 'repository.xds.services.endpoint'; 

function getEndpointRepoRUT(rut) {
	// repository endpoint by default
	var endpoint = $('repository.xds.services.endpoint');
	if(!isEmptyStr(rut)){
		var cad = new String(rut);
		var rutInteger = cad.replace(new RegExp('[^0-9]', 'g'), '');
		var rutEndpoint = textRepository + (rutInteger % $('repository.xds.instances'));
		if(!isEmptyStr($(rutEndpoint))){
			endpoint = $(rutEndpoint);
		}
	}
	return endpoint;
}

function getValueRUTPatient(xmlPatient){
	var value = "";
	try{
		var identifiers = xmlPatient.*::['identifier'];
		var iden;
		var rutBySystem;

		for each (identifier in identifiers){
			iden = new XML(identifier);
			rutBySystem = iden.*::['system'].@value.toString();
			if (!isEmptyStr(rutBySystem) && rutBySystem == $('system.id.control.rep')) {
				value = iden.*::['value'].@value.toString();
			} else{
				var rutByCoding = iden.*::['type'].*::['coding'].*::['code'].@value.toString();
				if(!isEmptyStr(rutByCoding) && rutByCoding == $('code.id.control.rep')){
					value = iden.*::['value'].@value.toString();
				}
			}	
		}
	}catch(err){
		channelMap.put("xdsCTxmlPatient",xmlPatient);
		logger.info("[HN] XDS - template: It has not been possible to get the patient's identifier");
	}
	return value;
}

function getEndpointRepoID(uniqueId) {
	// repository endpoint by default
	var endpoint = $('repository.xds.services.endpoint');
	if(!isEmptyStr(uniqueId)){
		var rutEndpoint = $(uniqueId);
		if(!isEmptyStr(rutEndpoint)){
			endpoint = rutEndpoint;
		}
	}
	return endpoint;
}

function isBase64(document){
	try {
    		Packages.java.util.Base64.getDecoder().decode(document.getBytes());
    		return true;
	} catch(err) {
    		return false;
	}
}

importPackage(org.apache.http.impl.client);
importPackage(org.apache.http.conn.ssl);
importPackage(org.apache.http.client);
importPackage(javax.net.ssl);


function setProtocolHTTPS() {
	//Establecemos para protocolo seguro por defecto que sea gestionado por la clase SSLProtocolSocketFactory
	//Esta clase ya buscar en el cacerts de la maquina virtual el certificado y usara la keyStore y trustStore como deba.
	var myhttps = new Packages.org.apache.commons.httpclient.protocol.Protocol("https", new Packages.org.apache.commons.httpclient.protocol.SSLProtocolSocketFactory(), 443)
    Packages.org.apache.commons.httpclient.protocol.Protocol.registerProtocol("https", myhttps);
}

function getHttpClient() {
	var builder = HttpClientBuilder.create();
	var sslContext = Packages.org.apache.http.ssl.SSLContextBuilder.create().loadTrustMaterial(new TrustSelfSignedStrategy()).build();
	var allowAllHosts = new NoopHostnameVerifier();
	var connectionFactory = new SSLConnectionSocketFactory(sslContext, allowAllHosts);
	builder.setSSLSocketFactory(connectionFactory);				
	var client = builder.build();
	return client;
}

function adtToPatient(adtMessage) {	
	var msh = adtMessage.MSH;
	var pid = adtMessage.PID;
	var patient = <Patient></Patient>;
	mapNationality(pid, patient);
	mapActive(msh, patient);
	mapIdentifiers(pid, patient);
	mapNames(pid, patient);
	mapTelecoms(pid, patient);
	mapGender(pid, patient);
	mapBirthDate(pid, patient);
	mapAddress(pid, patient);
	mapManagingOrganization(msh, patient);
	mapMaritalStatus(pid, patient);
	mapPaisNacimiento(pid, patient);
	mapExtensiones(pid, patient);
	return patient;
}

function mapNationality(pid, patient){
	try{
		for each (pid28 in pid['PID.28']){
			var nationality = pid28['PID.28.1'].toString();
			if (nationality.length > 0){
				patient.appendChild(createExtensionCodeNationality('http://hl7.org/fhir/StructureDefinition/patient-nationality', nationality));
			} 		
		}
	} catch (err) {		
	}
}

function mapPaisNacimiento(pid, patient){
	try{
		var campo = pid['PID.23']['PID.23.1'].toString();
		if (campo.length > 0){
			patient.appendChild(createExtensionCodePaisNac('http://hn.indra.es/fhir/StructureDefinition/patient-birthcountry', campo));
		} 		
	} catch (err) {		
	}
}


function mapMaritalStatus(pid, patient){	
	try{
		var maritalStatus = pid['PID.16']['PID.16.1'].toString();		
		if (maritalStatus.length > 0){			
			patient.maritalStatus.coding.code.@value = maritalStatus;
		} 		
	} catch (err) {		
	}
}

