function mapActive(msh, patient){

	try{
		if (msh['MSH.9']['MSH.9.2'] != 'A29'){
			patient.active.@value = true;
		}else {
			patient.active.@value = false;
		}
	} catch (err) {}		
}

function mapIdentifiers(pid, patient){
	try{
		for each (pid3 in pid['PID.3']){
			patient.appendChild(createIdentifier(pid3, 'PID.3'));
		}
	} catch (err) {}		
}

function mapNames(pid, patient){
	try{
		for each (pid5 in pid['PID.5']){
			patient.appendChild(createName(pid5));
		}
	} catch (err) {}		
}

function mapTelecoms(pid, patient){
	try{
		for each (pid13 in pid['PID.13']){
			patient.appendChild(createTelecom(pid13));
		}
	} catch (err) {}		
	try{
		for each (pid14 in pid['PID.14']){
			patient.appendChild(createTelecom2(pid14));
		}
	} catch (err) {}		
}

function mapGender(pid, patient){
	try{
		patient.gender.@value = map('administrativeGender', 'HL7', 'FHIR', pid['PID.8']['PID.8.1'].toString());
	} catch (err) {}	
}

function mapBirthDate(pid, patient){	
	try{
		var date = pid['PID.7']['PID.7.1'];	
		date = convertDateToFhir(date);		
		patient.birthDate.@value = date;			
	} catch (err) { }	

}

function mapAddress(pid, patient){
	try{
		for each (pid11 in pid['PID.11']){
			var descriptionCity = pid['PID.11']['PID.11.3']['PID.11.3.2'].toString();
			var descriptionState = pid['PID.11']['PID.11.4']['PID.11.4.2'].toString();
			var address = createAddress(pid11, descriptionCity,descriptionState);
			var codeCity = pid['PID.11']['PID.11.3']['PID.11.3.1'].toString();
			var codeState = pid['PID.11']['PID.11.4']['PID.11.4.1'].toString();
			address.appendChild(createExtensionString('http://hn.indra.es/fhir/StructureDefinition/address-state-code',codeState));
			address.appendChild(createExtensionString('http://hn.indra.es/fhir/StructureDefinition/address-city-code',codeCity));
			patient.appendChild(address);
		}
	} catch (err) {}		
}

function mapManagingOrganization(msh, patient){
	try{
		patient.managingOrganization.display.@value = msh['MSH.3']['MSH.3.1'];
	} catch (err) {}		
}

function createIdentifier(cx, field){
	var identifier = new XML(<identifier></identifier>);

	identifier.use.@value = 'official';
	var pid342 =  cx[field +'.4'][field +'.4.2'];	
	system = map('PID.3.4.2', 'HL7', 'FHIR', pid342);		
	identifier.system.@value = system;
	identifier.value.@value = cx[field +'.1'].toString();
	identifier.type.coding.code.@value = cx[field +'.5'].toString();	
	identifier.type.text.@value=cx[field +'.4'][field +'.4.1']+'';
	return identifier;
}



function createName(pid5){
	var name = new XML(<name></name>);	
	name.use.@value = 'official';			
	var given = pid5['PID.5.2'];
	try{

		if (pid5['PID.5.3'].toString().length > 0){			
			given = given + ' ' + pid5['PID.5.3'];						
		}

	} catch (err) {}
	name.given.@value = given;
	//name.family.@value = $f('%s %s', pid5['PID.5.1'], msg['PID']['PID.6']['PID.6.1']);	
	//name.family.@value = $f('%s', pid5['PID.5.1']);	
	name.appendChild(createExtensionString('http://hl7.org/fhir/StructureDefinition/humanname-fathers-family', pid5['PID.5.1']));

	var apellido2 = msg['PID']['PID.6']['PID.6.1'].toString();
	if (apellido2.length > 0){
		name.appendChild(createExtensionString('http://hl7.org/fhir/StructureDefinition/humanname-mothers-family',msg['PID']['PID.6']['PID.6.1']));
	}

	//name.text.@value = $f('%s %s', name.given.@value, name.family.@value);

	return name;
}


function createTelecom(xtn){
	if(xtn != ''){
		var telecom = new XML(<telecom></telecom>);		
		var type = xtn['PID.13.3']+'';
		if (type.length == 0){
			return '';
		}
		telecom.system.@value = map('contactPointSystem', 'HL7', 'FHIR', type);
		if (type == 'Internet'){
			telecom.value.@value = xtn['PID.13.4'];	
		} else {
			telecom.value.@value = xtn['PID.13.7'];
		}		

		if (type == 'CP'){
			telecom.use.@value = 'mobile';
		} else {
			//telecom.use.@value = map('contactPointUse', 'HL7', 'FHIR', xtn['PID.13.2']);
			telecom.use.@value = 'home';
		}		

		return telecom;
	}
	return '';
}

function createTelecom2(xtn){
	if(xtn != ''){
		var telecom = new XML(<telecom></telecom>);		
		var type = xtn['PID.14.3']+'';
		if (type.length == 0){
			return '';
		}
		telecom.system.@value = map('contactPointSystem', 'HL7', 'FHIR', type);
		if (type == 'Internet'){
			telecom.value.@value = xtn['PID.14.4'];	
		} else {
			telecom.value.@value = xtn['PID.14.7'];
		}		

		if (type == 'CP'){
			telecom.use.@value = 'mobile';
		} else {
			//telecom.use.@value = map('contactPointUse', 'HL7', 'FHIR', xtn['PID.14.2']);
			telecom.use.@value = 'work';
		}		

		return telecom;
	}
	return '';
}

function createExtensionString(url, value){
	var extension = <extension></extension>;
	extension.@url = url;
	var extensionValue = <valueString></valueString>;
	extensionValue.@value = value;
	extension.appendChild(extensionValue);
	return extension;
}


function createExtensionDateTime(url, value){
	var extension = <extension></extension>;
	extension.@url = url;
	var extensionValue = <valueDateTime></valueDateTime>;
	extensionValue.@value = value;
	extension.appendChild(extensionValue);
	return extension;
}

function createExtensionDate(url, value){
	var extension = <extension></extension>;
	extension.@url = url;
	var extensionValue = <valueDate></valueDate>;
	extensionValue.@value = value;
	extension.appendChild(extensionValue);
	return extension;
}

function createExtensionCode(url, value){
	var extension = <extension></extension>;
	extension.@url = url;
	var extensionCode = <extension></extension>;
	var valueCode = <valueCodeableConcept></valueCodeableConcept>
	var coding = <coding></coding>
	coding.code.@value=value;
	valueCode.appendChild(coding); 
	extensionCode.appendChild(valueCode); 	
	extension.appendChild(extensionCode);	
	return extension;
}

function createExtensionCodeIdentity(url, value){		
	var extension = <extension></extension>;
	extension.@url = url;
	var extensionCode = <extension></extension>;
	extensionCode.@url='code';
	var valueCode = <valueCodeableConcept></valueCodeableConcept>
	var coding = <coding></coding>
	coding.code.@value=value;
	valueCode.appendChild(coding); 
	extensionCode.appendChild(valueCode); 	
	extension.appendChild(extensionCode);
	logger.debug("extension: " + extension);
	return extension;
}

function createExtensionCodeNationality(url, value){		
	var extension = <extension></extension>;
	extension.@url = url;
	var extensionCode = <extension></extension>;
	extensionCode.@url='code';
	var valueCode = <valueCodeableConcept></valueCodeableConcept>
	var coding = <coding></coding>
	coding.code.@value=value;
	valueCode.appendChild(coding); 
	extensionCode.appendChild(valueCode); 	
	extension.appendChild(extensionCode);
	logger.debug("extension: " + extension);
	return extension;
}

function createExtensionCodePaisNac(url, value){
	var extension = <extension></extension>;
	extension.@url = url;	
	var valueCode = <valueCodeableConcept></valueCodeableConcept>
	var coding = <coding></coding>
	coding.code.@value=value;
	coding.system.@value='urn:hn:patient-birthcountry';
	valueCode.appendChild(coding); 	
	extension.appendChild(valueCode);	
	return extension;
}



function createAddress(xad,descriptionCity,descriptionState){
	var address = <address></address>;

	var use = xad['PID.11.7'];
	address.use.@value = map('addressUse', 'HL7', 'FHIR', xad['PID.11.7']);
	/*
	if (use == 'M'){
		address.type.@value = 'postal';
	}
	*/
	var line1 = <line></line>;
	var xad1=xad['PID.11.1'];	
	//line1.@value = $f('%s %s %s', xad1['PID.11.1.1'], xad1['PID.11.1.2'], xad1['PID.11.1.3']);
	line1.@value = xad1;
	address.appendChild(line1);

	if (xad['PID.11.2'] != ''){
		var line2 = <line></line>
		line2.@value = xad['PID.11.2'];
		address.appendChild(line2);
	}

	if (xad['PID.11.3'] != ''){
		//address.city.@value = xad['PID.11.3'];
		address.city.@value = descriptionCity;
	}
	if (xad['PID.11.9'] != ''){
		address.district.@value = xad['PID.11.9'];
	}
	//address.state.@value = xad['PID.11.4'];
	address.state.@value = descriptionState;
	address.postalCode.@value = xad['PID.11.5'];
	address.country.@value = xad['PID.11.6'];

	return address;
}

function adtToPatientProject(adtMessage) {	
	var msh = adtMessage.MSH;
	var pid = adtMessage.PID;
	var patient = <Patient></Patient>;	
	mapNationality(pid, patient);	
	mapActive(msh, patient);	
	mapIdentifiers(pid, patient);	
	mapNames(pid, patient);	
	mapTelecoms(pid, patient);	
	mapGender(pid, patient);
	mapBirthDate(pid, patient);
	mapAddress(pid, patient);
	mapManagingOrganization(msh, patient);
	mapMaritalStatus(pid, patient);
	mapPrimaryLanguage(pid, patient);
	mapPaisNacimiento(pid, patient);
	mapExtensiones(pid, patient);
	return patient;
}

function mapNationality(pid, patient){
	try{
		for each (pid28 in pid['PID.28']){
			var nationality = pid28['PID.28.1'].toString();
			if (nationality.length > 0){
				patient.appendChild(createExtensionCodeNationality($g('gsexturlnationality'), nationality));
			} 		
		}
	} catch (err) {		
	}
}

function mapPaisNacimiento(pid, patient){
