function mapActive(msh, patient){

	try{
		if (msh['MSH.9']['MSH.9.2'] != 'A29'){
			patient.active.@value = true;
		}else {
			patient.active.@value = false;
		}
	} catch (err) {}		
}

function mapIdentifiers(pid, patient){
	try{
		for each (pid3 in pid['PID.3']){
			patient.appendChild(createIdentifier(pid3, 'PID.3'));
		}
	} catch (err) {}		
}

