function mapAddress(pid, patient){
	try{
		for each (pid11 in pid['PID.11']){
			var descriptionCity = pid['PID.11']['PID.11.3']['PID.11.3.2'].toString();
			var descriptionState = pid['PID.11']['PID.11.4']['PID.11.4.2'].toString();
			var address = createAddress(pid11, descriptionCity,descriptionState);
			var codeCity = pid['PID.11']['PID.11.3']['PID.11.3.1'].toString();
			var codeState = pid['PID.11']['PID.11.4']['PID.11.4.1'].toString();
			address.appendChild(createExtensionString($g('gsexturladdrstatecode'),codeState));
			address.appendChild(createExtensionString($g('gsexturladdrcitycode'),codeCity));
			patient.appendChild(address);
		}
	} catch (err) {}		
}

