function createIdentifier(cx, field){
	var identifier = new XML(<identifier></identifier>);

	identifier.use.@value = 'official';
	var pid342 =  cx[field +'.4'][field +'.4.2'];	
	system = map('PID.3.4.2', 'HL7', 'FHIR', pid342);		
	identifier.system.@value = system;
	identifier.value.@value = cx[field +'.1'].toString();
	identifier.type.coding.code.@value = cx[field +'.5'].toString();	
	identifier.type.text.@value=cx[field +'.4'][field +'.4.1']+'';

	// Tratamiento para determinados tipos de documentos sean tratados como guardian
	var aIdTypesforGuardian = ['827884'];
	var aSystem = system.split('.');
	var iNumSystemElem = aSystem.length;
	var bGuardian = ( iNumSystemElem > 0 && aIdTypesforGuardian.indexOf(aSystem[iNumSystemElem - 1]) > -1 );
	identifier.appendChild( createExtensionBoolean("http://hn.indra.es/fhir/StructureDefinition/identifier-guardian", bGuardian) );

	//TODO introducir aqu la lgica para el tratamiento del pas de emisin

	return identifier;
}

function createName(pid5){	
	var name = new XML(<name></name>);	
	name.use.@value = 'official';			
	var given = pid5['PID.5.2'];
	try{

		if (pid5['PID.5.3'].toString().length > 0){			
			given = given + ' ' + pid5['PID.5.3'];						
		}

	} catch (err) {}
	name.given.@value = given;	
	//name.family.@value = $f('%s %s', pid5['PID.5.1'], msg['PID']['PID.6']['PID.6.1']);	
	//name.family.@value = $f('%s', pid5['PID.5.1']);	
	var apellido1 = pid5['PID.5.1'];		
	name.appendChild(createExtensionString($g('gsexturlfatherfamilyname'), pid5['PID.5.1']));	
	var apellido2 = msg['PID']['PID.6']['PID.6.1'].toString();	
	if (apellido2.length > 0){
		name.appendChild(createExtensionString($g('gsexturlmotherfamilyname'),msg['PID']['PID.6']['PID.6.1']));
		name.text.@value = $f('%s %s %s', name.given.@value, apellido1, apellido2);
	} else {
		name.text.@value = $f('%s %s', name.given.@value, apellido1);
	}	
	return name;
}


function createTelecom(xtn){
	if(xtn != ''){
		var telecom = new XML(<telecom></telecom>);		
		var type = xtn['PID.13.3']+'';
		if (type.length == 0){
			return '';
		}
		telecom.system.@value = map('contactPointSystem', 'HL7', 'FHIR', type);
		if (type == 'Internet'){
			telecom.value.@value = xtn['PID.13.4'];	
		} else {
			telecom.value.@value = xtn['PID.13.7'];
		}		

		if (type == 'CP'){
			telecom.use.@value = 'mobile';
		} else {
			//telecom.use.@value = map('contactPointUse', 'HL7', 'FHIR', xtn['PID.13.2']);
			telecom.use.@value = 'home';
		}		

		return telecom;
	}
	return '';
}

function createTelecom2(xtn){
	if(xtn != ''){
		var telecom = new XML(<telecom></telecom>);		
		var type = xtn['PID.14.3']+'';
		if (type.length == 0){
			return '';
		}
		telecom.system.@value = map('contactPointSystem', 'HL7', 'FHIR', type);
		if (type == 'Internet'){
			telecom.value.@value = xtn['PID.14.4'];	
		} else {
			telecom.value.@value = xtn['PID.14.7'];
		}		

		if (type == 'CP'){
			telecom.use.@value = 'mobile';
		} else {
			//telecom.use.@value = map('contactPointUse', 'HL7', 'FHIR', xtn['PID.14.2']);
			telecom.use.@value = 'work';
		}		

		return telecom;
	}
	return '';
}

function createExtensionString(url, value){
	var extension = <extension></extension>;
	extension.@url = url;
	var extensionValue = <valueString></valueString>;
	extensionValue.@value = value;
	extension.appendChild(extensionValue);
	return extension;
}

